mapper.select:
/* User 테이블 가져오기 UserMapper.getUserList */
SELECT *
FROM monitoring_db.monitoring_admin

mapper.insert:
/* 회원가입 UserMapper.insertUser */
INSERT INTO monitoring_db.monitoring_admin (ID, PASSWORD, USERNAME, TEL, AUTHOR, MEMO)
VALUES (#{id}, #{password}, #{username}, #{tel}, #{author}, #{memo})

mapper.select:
/* 회원 정보 가져오기 UserMapper.getUserById */
SELECT *
FROM monitoring_db.monitoring_admin
WHERE ID = #{id}

mapper.update:
/* 회원정보 수정 UserMapper.updateUser */
UPDATE monitoring_db.monitoring_admin
SET USERNAME = #{username},
TEL = #{tel},
AUTHOR = #{author}

    ,PASSWORD = #{password}

WHERE ID = #{id}

mapper.delete:
/* 탈퇴 UserMapper.deleteUser */
DELETE
FROM monitoring_db.monitoring_admin
WHERE ID = #{id}

mapper.select:
/* 계정존재여부확인 UserMapper.getUserDetails */
SELECT id,
       password
FROM monitoring_db.monitoring_admin
WHERE id = #{id}
